document.addEventListener("DOMContentLoaded",()=>{var e=(t,l,r)=>{fetch(t).then(e=>{if(e.ok)return e.text();throw new Error("Failed to load "+t)}).then(e=>{var t=document.querySelector(l);t&&(t.innerHTML=e,r)&&r()}).catch(e=>console.error(`Error loading ${t}:`,e))};e("header.html","header.page-header",()=>{let l=window.location.pathname.split("/").pop();var e=document.querySelectorAll("header.page-header .nav-link");let r={Menu:["food.html","entrees.html","desserts.html","drinks.html","formules.html","english-menu.html"],Album:["gallery.html","gallery-food.html","gallery-desserts.html","gallery-location.html"]};e.forEach(e=>{let t=e.getAttribute("href");t!==l&&!Object.keys(r).some(e=>r[e].includes(l)&&r[e].includes(t))||e.parentElement.classList.add("active")})}),e("footer.html","footer.page-footer")});